import streamlit as st
from functions import get_liked_tweets, RedditCategoryPredictor, get_most_similar_subreddits
import pandas as pd

sidebar = st.sidebar.radio("Check these out!", ('Twitter-2-Reddit Suggestor','See Related Subreddits'))
if sidebar == 'Twitter-2-Reddit Suggestor':
    st.title('SubReddit Suggester')
    st.markdown('>This app takes your Twitter handle and suggests subreddits to you based on your liked tweets!')
    st.markdown("<div align='center'><br>"
                    "<img src='data:image/jpeg;base64,'"
                    "alt='TwitterReddit' height='400'/></div>", unsafe_allow_html=True)
    RedditCategoryPredictor(st.text_input("What is your Twitter handle (@) ?"))

indexed_subs_df = pd.read_csv('related_subs_and_indexes')
if sidebar == 'See Related Subreddits':
    st.title('Subreddit Similarity Checker')
    subreddit = st.selectbox("Pick a subreddit and find similar ones", indexed_subs_df['sub'])
    st.table(get_most_similar_subreddits(subreddit))